{
  "swagger": "2.0",
  "info": {
    "title": "OpenAPI Document on Azure Functions (Injected)",
    "description": "This is the OpenAPI Document on Azure Functions",
    "termsOfService": "https://github.com/Azure/azure-functions-openapi-extension",
    "contact": {
      "name": "Enquiry",
      "url": "https://github.com/Azure/azure-functions-openapi-extension/issues",
      "email": "azfunc-openapi@microsoft.com"
    },
    "license": {
      "name": "MIT",
      "url": "http://opensource.org/licenses/MIT"
    },
    "version": "1.0.0"
  },
  "host": "localhost:7202",
  "basePath": "/api",
  "schemes": [
    "http"
  ],
  "paths": {
    "/GetAll": {
      "get": {
        "tags": [
          "Products"
        ],
        "operationId": "Products_GetAll",
        "produces": [
          "application/json"
        ],
        "responses": {
          "200": {
            "description": "Payload of ProductResponse",
            "schema": {
              "$ref": "#/definitions/productResponse"
            }
          }
        }
      }
    },
    "/Checkout": {
      "post": {
        "tags": [
          "Stripe"
        ],
        "operationId": "Stripe_Checkout",
        "consumes": [
          "application/json"
        ],
        "produces": [
          "application/json"
        ],
        "parameters": [
          {
            "in": "body",
            "name": "body",
            "required": true,
            "schema": {
              "$ref": "#/definitions/checkoutRequest"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "Payload of CheckoutResponse",
            "schema": {
              "$ref": "#/definitions/checkoutResponse"
            }
          }
        }
      }
    },
    "/Create": {
      "post": {
        "tags": [
          "Users"
        ],
        "operationId": "Users_Create",
        "consumes": [
          "application/json"
        ],
        "produces": [
          "application/json"
        ],
        "parameters": [
          {
            "in": "body",
            "name": "body",
            "required": true,
            "schema": {
              "$ref": "#/definitions/user"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "Payload of User",
            "schema": {
              "$ref": "#/definitions/user"
            }
          }
        }
      }
    },
    "/Get": {
      "get": {
        "tags": [
          "Users"
        ],
        "operationId": "Users_Get",
        "produces": [
          "application/json"
        ],
        "responses": {
          "200": {
            "description": "Payload of User",
            "schema": {
              "$ref": "#/definitions/user"
            }
          }
        }
      }
    }
  },
  "definitions": {
    "checkoutRequest": {
      "type": "object",
      "properties": {
        "type": {
          "type": "string"
        },
        "successUrl": {
          "type": "string"
        },
        "cancelUrl": {
          "type": "string"
        },
        "lineItems": {
          "type": "array",
          "items": {
            "$ref": "#/definitions/lineItem"
          }
        }
      }
    },
    "checkoutResponse": {
      "type": "object",
      "properties": {
        "url": {
          "type": "string"
        }
      }
    },
    "lineItem": {
      "type": "object",
      "properties": {
        "priceId": {
          "type": "string"
        },
        "quantity": {
          "format": "int32",
          "type": "integer"
        }
      }
    },
    "productDto": {
      "type": "object",
      "properties": {
        "id": {
          "type": "string"
        },
        "name": {
          "type": "string"
        },
        "description": {
          "type": "string"
        },
        "image": {
          "type": "array",
          "items": {
            "type": "string"
          }
        },
        "defaultPriceId": {
          "type": "string"
        },
        "currency": {
          "type": "string"
        },
        "type": {
          "type": "string"
        }
      }
    },
    "productResponse": {
      "type": "object",
      "properties": {
        "products": {
          "type": "array",
          "items": {
            "$ref": "#/definitions/productDto"
          }
        }
      }
    },
    "user": {
      "type": "object",
      "properties": {
        "userName": {
          "type": "string"
        }
      }
    }
  }
}
